import React, { useState, useEffect } from 'react';
import {
    CButton,
    CCard,
    CCardBody,
    CCardFooter,
    CCardHeader,
    CCol,
    CForm,
    CFormGroup,
    CFormText,
    CTextarea,
    CInput,
    CLabel
  } from '@coreui/react'
import CIcon from '@coreui/icons-react'
import { forEach } from 'core-js/fn/array';

const CreateNode = () => {
    const [appName, setAppName] = useState("");
    const [hostName, setHostName] = useState("");
    const [owner, setOwner] = useState("");
    const [group, setGroup] = useState("");
    const [groupMail, setGroupMail] = useState("");
    const [desciption, setDesciption] = useState("");
    const [warningModal, setWarningModal] = useState(false);

    const creatNodeReq = async (e) => {
        let valid = true;

        const payload = {
            appName: appName,
            hostName: hostName,
            owner: owner,
            group: group,
            groupMail: groupMail,
            desciption: desciption
        }

        for(var key in payload){
            if(!payload[key]) return;
        }

        for(var key in payload){
            if(payload[key] != '' && !payload[key]){
                this.$vux.toast.text('请完善数据!', 'middle');
                return false; // 终止程序
            }

        }
        return valid;
    };

    return (
        <>
            <CCard>
                <CCardHeader>
                    Create a new node
                </CCardHeader>
                <CCardBody>
                    <CForm onSubmit={(e) => creatNodeReq(e)} action="" method="post" encType="multipart/form-data" className="form-horizontal">
                    <CFormGroup row>
                        <CCol md="3">
                        <CLabel htmlFor="text-input">Application Name</CLabel>
                        </CCol>
                        <CCol xs="12" md="9">
                        <CInput
                            type="input"
                            placeholder="Enter name of your app"
                            required
                            value={appName}
                            onChange={(e) => setAppName(e.target.value)}
                            // id="text-input" name="text-input"  
                        />
                        </CCol>
                    </CFormGroup>
                    <CFormGroup row>
                        <CCol md="3">
                        <CLabel htmlFor="email-input">Host Name</CLabel>
                        </CCol>
                        <CCol xs="12" md="9">
                        <CInput
                            type="input"
                            placeholder="Enter host name"
                            required
                            value={hostName}
                            onChange={(e) => setHostName(e.target.value)}
                            // id="email-input" name="email-input"
                        />
                        {/* <CFormText className="help-block">Enter host/server name</CFormText> */}
                        </CCol>
                    </CFormGroup>
                    <CFormGroup row>
                        <CCol md="3">
                        <CLabel htmlFor="password-input">App Owner</CLabel>
                        </CCol>
                        <CCol xs="12" md="9">
                        <CInput 
                            type="input"
                            placeholder="Owner of this app (eg. group leader)"
                            required
                            value={owner}
                            onChange={(e) => setOwner(e.target.value)}
                            // id="password-input" name="password-input"
                        />
                        {/* <CFormText className="help-block">Owner of this app (eg. group leader)</CFormText> */}
                        </CCol>
                    </CFormGroup>
                    <CFormGroup row>
                        <CCol md="3">
                        <CLabel htmlFor="password-input">Group Name</CLabel>
                        </CCol>
                        <CCol xs="12" md="9">
                        <CInput 
                            type="input"
                            placeholder="Team developed and operated this app"
                            required
                            value={group}
                            onChange={(e) => setGroup(e.target.value)}
                            // id="password-input" name="password-input"
                        />
                        {/* <CFormText className="help-block">Team developed and operated this app</CFormText> */}
                        </CCol>
                    </CFormGroup>
                    <CFormGroup row>
                        <CCol md="3">
                        <CLabel htmlFor="email-input">Group Email</CLabel>
                        </CCol>
                        <CCol xs="12" md="9">
                        <CInput
                            type="email"
                            placeholder="Contact email of the group. xxx@xx.com"
                            required
                            value={groupMail}
                            onChange={(e) => setGroupMail(e.target.value)}
                            // id="email-input" name="email-input" autoComplete="email"
                        />
                        {/* <CFormText className="help-block">Contact email of the group</CFormText> */}
                        </CCol>
                    </CFormGroup>
                    <CFormGroup row>
                        <CCol md="3">
                        <CLabel htmlFor="textarea-input">Description</CLabel>
                        </CCol>
                        <CCol xs="12" md="9">
                        <CTextarea 
                            type="input"
                            placeholder="Briefly describe your app"
                            required
                            value={desciption}
                            onChange={(e) => setDesciption(e.target.value)}
                            // name="textarea-input" 
                            // id="textarea-input" 
                            rows="3"
                        />
                        </CCol>
                    </CFormGroup>
                    </CForm>
                </CCardBody>
                <CCardFooter className="c-datatable-filter">
                    <div className="mfs-auto">
                        <CButton type="submit" size="sm" color="success" className="mr-2"><CIcon name="cil-scrubber" /> Submit</CButton>
                        <CButton type="reset" size="sm" color="danger"><CIcon name="cil-ban" /> Reset</CButton>
                    </div>
                </CCardFooter>
            </CCard>

            <CModal 
              show={warningModal} 
              onClose={() => setWarningModal(!warningModal)}
              color="warning"
            >
              <CModalHeader closeButton>
                <CModalTitle>Invalid input</CModalTitle>
              </CModalHeader>
              <CModalBody>
                Please fill in all fields!
              </CModalBody>
              <CModalFooter>
                {/* <CButton color="warning" onClick={() => setWarning(!warning)}>Do Something</CButton>{' '} */}
                <CButton color="secondary" onClick={() => setWarningModal(!warningModal)}>Close</CButton>
              </CModalFooter>
            </CModal>
        </>
    )
}

export default CreateNode
